
public class Operater {
	public static void main(String[] args) {
			// TODO Auto-generated method stub
		int a = 10;
		int b = 3;
		double doubleb = 3;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		System.out.println(a/doubleb);

	}
}
	 
class plus{
	public static void main(String[] args) {
	int a = 1;
	System.out.println(a);
	a++;
	System.out.println(a);
	System.out.println(++a);
	System.out.println(a++);
	System.out.println(a);
	}
}